mapper.select:
select

from order_info
where 1=1
and isValid =1

    and orderTime >= #{startTime}


  and orderTime <= #{endTime}


  and username like  "%"#{username}"%"


  and tel like "%"#{tel}"%"


  and unit like "%"#{unit}"%"


  and accommodation = #{accommodation}


  and room_type = #{roomType}


  and move_into_time = #{moveIntoTime}


  and move_out_time = #{moveOutTime}


  and dock = #{dock}


  and isCheck = #{isCheck}


  and hotel = #{hotel}


  and dining = #{dining}


  and seat = #{seat}

order by orderTime desc

