mapper.select:
select
*
from message
where  1=1

    and fuctionCode = #{fuctionCode,jdbcType=VARCHAR}


    and telemetryAddress = #{telemetryAddress,jdbcType=VARCHAR}


    and centerAdress = #{centerAdress,jdbcType=VARCHAR}


  and createtime >=#{startTime}


  and createtime <=#{endTime}

order by createtime desc
