mapper.select:
select
*
from telemetry_switch_pump
where 1=1

  and fuctionCode = #{fuctionCode,jdbcType=VARCHAR}


  and telemetryAddress = #{telemetryAddress,jdbcType=VARCHAR}


  and centerAdress = #{centerAdress,jdbcType=VARCHAR}


  and createtime >=#{startTime}


  and createtime <=#{endTime}

order by createtime desc

SELECT * from
((SELECT * from `telemetry_switch_pump`)UNION ALL(SELECT *,null as cumulativeFlowRate,null as cumulativeElectricityRate,null as voltageA,null as voltageB,null as voltageC,null as electricA,null as electricB,null as electricC,null as userNumber,null as icCard,null as balance,null as waterConsumption,null as electricityConsumption,null as useTime,null as startTime,null as endTime,null as consumption,null as type,null as comtrol,null as cumulativeTime,null as voltage,null as csqSignalIntensity,null as instantaneousFlowRate from `telemetry_station_add`)union all(SELECT *,null as userNumber,null as icCard,null as balance,null as waterConsumption,null as electricityConsumption,null as useTime,null as startTime,null as endTime,null as consumption,null as type,null as comtrol from `telemetry_station_time`))
as i
where 1=1


    and fuctionCode = #{fuctionCode,jdbcType=VARCHAR}


    and fuctionCode in ('32','33','E0')



  and telemetryAddress = #{telemetryAddress,jdbcType=VARCHAR}


  and createtime >=#{startTime}


  and createtime <=#{endTime}

order by createTime DESC
